func:
var datos = msg.payload;
var topico = msg.topic;
var deviceId = "Desconocido";

if (topico) {
    var partes = topico.split("/");
    if (partes.length > 2) deviceId = partes[2];
}

msg.payload = [{
    measurement: "telemetria_farola",
    tags: {
        dispositivo: deviceId
    },
    fields: {
        voltaje_V: Number(datos.voltage_V),
        corriente_mA: Number(datos.current_mA),
        potencia_mW: Number(datos.power_mW),
        pir_state: Number(datos.pir_state),
        led_duty: Number(datos.led_duty)
    }
}];

return msg;
